using GameUI.Logic; using UnityEngine;  namespace Zenject.Installers {     public class GameWindowsHandlerInstaller : MonoInstaller     {         [SerializeField] private GameWindowsHandler _windowsHandlerPrefab;         public override void InstallBindings()         {             var handlerInstance = Container.InstantiatePrefabForComponent<GameWindowsHandler>(_windowsHandlerPrefab);             var cursorSwitcherInstance = handlerInstance.GetComponentInChildren<CursorController>();              Container.Bind<GameWindowsHandler>().FromInstance(handlerInstance).AsSingle().NonLazy();             Container.Bind<CursorController>().FromInstance(cursorSwitcherInstance).AsSingle();              handlerInstance.Container = Container;             handlerInstance.transform.SetParent(null);         }     } }